CREATE OR REPLACE FUNCTION obter_data_proc_matpaci ( nr_seq_procedimento_p bigint, nr_seq_material_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


ds_retorno_w	timestamp;
dt_execucao_w	timestamp;


BEGIN

if (nr_seq_procedimento_p IS NOT NULL AND nr_seq_procedimento_p::text <> '') then

	select	dt_procedimento
	into STRICT	dt_execucao_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_procedimento_p;

elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then
	select	dt_conta
	into STRICT	dt_execucao_w
	from	material_atend_paciente
	where	nr_sequencia	= nr_seq_material_p;
end if;

if (ie_opcao_p	= 'DT') then
	ds_retorno_w	:= dt_execucao_w;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
