SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_data_propaci (nr_seq_proced_p text, ie_tipo_data_p text) RETURNS timestamp AS $body$
DECLARE


dt_procedimento_w		timestamp;
dt_conta_w			timestamp;
dt_conferencia_w		timestamp;


BEGIN

SELECT	dt_procedimento,
	dt_conta,
	dt_conferencia
INTO STRICT	dt_procedimento_w,
	dt_conta_w,
	dt_conferencia_w
FROM	procedimento_paciente
WHERE	nr_sequencia	= nr_seq_proced_p;

IF (ie_tipo_data_p	= 'A') then
	return	dt_procedimento_w;
end if;

if (ie_tipo_data_p = 'R') then
	return dt_conferencia_w;
else
	return	dt_conta_w;
end if;


END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
