CREATE OR REPLACE FUNCTION obter_data_prox_versao_escala (dt_base timestamp) RETURNS varchar AS $body$
DECLARE


dt_liberacao_w	varchar(10);


BEGIN

	select	to_char(min(d.dt_fim), 'dd/mm/yyyy')
	into STRICT	dt_liberacao_w
	from	escala_diaria d
	where	d.nr_seq_escala = 21
	and		d.dt_inicio > dt_base;


return	dt_liberacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
