CREATE OR REPLACE FUNCTION obter_data_real_quimio_aut (nr_seq_atendimento_p bigint, nr_seq_paciente_p bigint, nr_ciclo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

	begin
	select	max(dt_real)
	into STRICT	dt_retorno_w
	from	paciente_atendimento
	where	nr_seq_atendimento = nr_seq_atendimento_p;
	exception
	when others then
		dt_retorno_w := null;
	end;

elsif (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') then
	begin
	select	max(dt_real)
	into STRICT	dt_retorno_w
	from	paciente_atendimento
	where	nr_seq_paciente = nr_seq_paciente_p
	and	nr_ciclo	= nr_ciclo_p;
	exception
	when others then
		dt_retorno_w := null;
	end;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
