CREATE OR REPLACE FUNCTION obter_data_receb_adiant (nr_adiantamento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_recebimento_w	timestamp := null;


BEGIN

if (nr_adiantamento_p IS NOT NULL AND nr_adiantamento_p::text <> '' AND ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '') then

	select	min(dt_transacao)
	into STRICT	dt_recebimento_w
	from	movto_trans_financ a
	where	a.nr_adiantamento	= nr_adiantamento_p
	and	(((a.nr_seq_caixa IS NOT NULL AND a.nr_seq_caixa::text <> '') and ie_opcao_p = 'T') or ((a.nr_seq_saldo_banco IS NOT NULL AND a.nr_seq_saldo_banco::text <> '') and ie_opcao_p = 'B'));

	if (coalesce(dt_recebimento_w::text, '') = '' and ie_opcao_p = 'B') then

		select	min(a.dt_transacao)
		into STRICT	dt_recebimento_w
		from	movto_trans_financ a,
			movto_banco_pend b,
			movto_banco_pend_baixa c
		where	a.nr_sequencia = b.nr_seq_movto_trans_fin
		and	b.nr_sequencia = c.nr_seq_movto_pend
		and	c.nr_adiantamento = nr_adiantamento_p;

	end if;

end if;

return dt_recebimento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
