CREATE OR REPLACE FUNCTION obter_data_ref_protocolo (cd_convenio_p bigint, ie_tipo_protocolo_p text) RETURNS timestamp AS $body$
DECLARE


dt_referencia_w		timestamp;


BEGIN

select	max(trunc(dt_ref_valida))
into STRICT	dt_referencia_w
from	conv_fechamento_tipo_prot
where	cd_convenio		= cd_convenio_p
and	ie_tipo_protocolo	= ie_tipo_protocolo_p;

if (coalesce(dt_referencia_w::text, '') = '') then
	select	max(trunc(dt_ref_valida))
	into STRICT	dt_referencia_w
	from	convenio
	where	cd_convenio	= cd_convenio_p;
end if;

return	dt_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
