CREATE OR REPLACE FUNCTION obter_data_reg_comparecimento ( cd_tipo_controle_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


dt_entrada_saida_w			timestamp;
nr_sequencia_w				bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from	controle_pessoa_comp
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	cd_tipo_controle = cd_tipo_controle_p;

if (nr_sequencia_w > 0) then

	if (ie_opcao_p = 'E') then
		select	dt_entrada
		into STRICT	dt_entrada_saida_w
		from	controle_pessoa_comp
		where	nr_sequencia = nr_sequencia_w;
	elsif (ie_opcao_p = 'S') then
		select	dt_saida
		into STRICT	dt_entrada_saida_w
		from	controle_pessoa_comp
		where	nr_sequencia = nr_sequencia_w;
	end if;
end if;

return	dt_entrada_saida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
