CREATE OR REPLACE FUNCTION obter_data_result_cultura (nr_prescricao_p bigint) RETURNS timestamp AS $body$
DECLARE

dt_resultado_w timestamp;
nr_prescricao_w bigint;


BEGIN
if (coalesce(nr_prescricao_p, 0) > 0) then

	select	substr(max(a.dt_resultado), 1,255),
		max(b.nr_prescricao)
	into STRICT	dt_resultado_w,
		nr_prescricao_w
	from	exame_lab_resultado a,
		cih_precaucao_cultura b
	where	a.nr_prescricao = nr_prescricao_p
	and	a.nr_prescricao  = b.nr_prescricao;
end if;

return	dt_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
