CREATE OR REPLACE FUNCTION obter_data_resumo_html5 ( nr_seq_resumo_html5_p bigint, ds_dano_breve_os_p text, ie_tipo_data_p text, dt_referencia_p timestamp default clock_timestamp()) RETURNS timestamp AS $body$
DECLARE


/*
IE_TIPO_DATA_P
	A = Análise,
	P = Programação
*/
dt_retorno_w		timestamp;
nr_seq_funcao_w		bigint;
qt_previsao_w		double precision;
qt_capacidade_w		double precision;
nr_seq_equipe_des_w	bigint;


BEGIN

dt_retorno_w := null;

if (ie_tipo_data_p = 'P') then
	nr_seq_funcao_w := 44;
elsif (ie_tipo_data_p = 'A') then
	nr_seq_funcao_w := 45;
end if;

select	max(nr_seq_equipe_des)
into STRICT	nr_seq_equipe_des_w
from	resumo_html5
where	nr_sequencia = nr_seq_resumo_html5_p;

select	sum(CASE WHEN nr_seq_funcao_w=44 THEN  b.qt_prev_os_prog  ELSE b.qt_prev_os_analise END )
into STRICT	qt_previsao_w
from	resumo_html5_os b
where	b.nr_seq_resumo_html5 = nr_seq_resumo_html5_p
and	upper(b.ds_dano_breve) like upper(CASE WHEN coalesce(ds_dano_breve_os_p::text, '') = '' THEN  b.ds_dano_breve  ELSE '%'||ds_dano_breve_os_p||'%' END );

select	sum(c.qt_horas_rec_proj)
into STRICT	qt_capacidade_w
from	proj_equipe_papel c
where	c.nr_seq_funcao = nr_seq_funcao_w
and	c.ie_situacao = 'A'
and	c.nr_seq_equipe = nr_seq_equipe_des_w;

if (qt_capacidade_w IS NOT NULL AND qt_capacidade_w::text <> '') and (qt_capacidade_w > 0) then
	dt_retorno_w := add_working_days(dt_retorno_w, ceil(qt_previsao_w/qt_capacidade_w));
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
