CREATE OR REPLACE FUNCTION obter_data_rn_setor ( nr_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE


qt_hora_retorno_w		bigint;
nr_seq_atepacu_w		atend_paciente_unidade.nr_seq_interno%type;


BEGIN

select	coalesce(max(a.nr_seq_interno),0)
into STRICT	nr_seq_atepacu_w
from	atend_paciente_unidade  a,
		setor_atendimento 	b
where	a.nr_atendimento = nr_atendimento_p
and 	a.cd_setor_atendimento = b.cd_setor_atendimento
and (b.cd_classif_setor = 9);

if (nr_seq_atepacu_w > 0) then
	select	pkg_date_utils.get_DiffDate(dt_entrada_unidade,coalesce(dt_saida_unidade,clock_timestamp()),'HOUR')
	into STRICT	qt_hora_retorno_w
	from	atend_paciente_unidade
	where	nr_seq_interno = nr_seq_atepacu_w;
end if;

return	qt_hora_retorno_w;

end;
