CREATE OR REPLACE FUNCTION obter_data_sus_unif ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
I - Data de inicio da validade
F - Data final da validade
T - Data competencia da apac
*/
dt_inicio_validade_w	varchar(50);
dt_fim_validade_w		varchar(50);
dt_competencia_w        varchar(50);


BEGIN

if (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') then

	select	to_char(dt_inicio_validade,'dd/mm/yyyy hh24:mi:ss'),
		to_char(dt_fim_validade,'dd/mm/yyyy hh24:mi:ss'),
        to_char(dt_competencia,'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_inicio_validade_w,
		dt_fim_validade_w,
        dt_competencia_w
	from	sus_apac_unif
	where	nr_interno_conta = nr_interno_conta_p;
	
	if (ie_opcao_p = 'I') then
		return dt_inicio_validade_w;
	elsif (ie_opcao_p = 'F') then
		return dt_fim_validade_w;
	elsif (ie_opcao_p = 'T') then
		return dt_competencia_w;
	end if;
	
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
