CREATE OR REPLACE FUNCTION obter_data_ultimo_alt_pep ( cd_estabelecimento_p bigint, nr_atendimento_p bigint, nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_ultimo_acesso_w 		timestamp;


BEGIN

-- get the last access or actual date minus 24 hours
select  coalesce(max(dt_atualizacao), (clock_timestamp() - interval '1 days'))
Into STRICT      dt_ultimo_acesso_w
from    pep_acesso
where   cd_estabelecimento = cd_estabelecimento_p
and     nr_atendimento = nr_atendimento_p
and     nm_usuario = nm_usuario_p
and     pkg_date_utils.end_of(dt_atualizacao, 'DAY') < pkg_date_utils.start_of(clock_timestamp(), 'DAY');

RETURN dt_ultimo_acesso_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
