CREATE OR REPLACE FUNCTION obter_data_ult_compra_fornec ( cd_cnpj_p text, cd_estabelecimento_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_entrada_saida_w			timestamp := null;


BEGIN

select	trunc(max(b.dt_entrada_saida),'dd')
into STRICT	dt_entrada_saida_w
from	operacao_nota o,
	nota_fiscal_item a,
	nota_fiscal b
where	a.nr_sequencia		= b.nr_sequencia
and	b.cd_operacao_nf		= o.cd_operacao_nf
and	b.cd_cgc_emitente		= cd_cnpj_p
and	coalesce(o.ie_ultima_compra, 'S')	= 'S'
and	b.cd_estabelecimento	= cd_estabelecimento_p;

return	dt_entrada_saida_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
