CREATE OR REPLACE FUNCTION obter_data_ult_compra_param ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_local_estoque_p bigint, ie_tipo_p text, qt_compra_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w			timestamp;
nr_sequencia_w			bigint;
ie_tipo_w				varchar(10);
ie_consignado_w			varchar(1);
cd_material_estoque_w		integer;
cont_w				bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	natureza_operacao o,
	operacao_nota p,
	nota_fiscal b,
	nota_fiscal_item a
where	a.nr_sequencia		= b.nr_sequencia
and	b.cd_natureza_operacao	= o.cd_natureza_operacao
and	o.ie_entrada_saida		= 'E'
and	cd_material  		= cd_material_p
and	b.cd_operacao_nf		= p.cd_operacao_nf
and	coalesce(p.ie_ultima_compra, 'S')	= 'S'
and	(a.dt_atualizacao IS NOT NULL AND a.dt_atualizacao::text <> '')
and	((coalesce(cd_local_estoque_p::text, '') = '') or (cd_local_estoque = cd_local_estoque_p))
and	a.cd_estabelecimento	= cd_estabelecimento_p
and	b.ie_situacao		= '1'
order by	a.nr_sequencia desc;


BEGIN
cont_w			:= 0;
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	cont_w := cont_w +1;
	if (cont_w = qt_compra_p) then
		exit;
	else
		nr_sequencia_w := 0;
	end if;
	end;
end loop;
close C01;

if (nr_sequencia_w > 0) then
	select	max(b.dt_entrada_saida)
	into STRICT	dt_retorno_w
	from 	nota_fiscal_item a,
		nota_fiscal b
	where	a.nr_sequencia 	= b.nr_sequencia
	and	b.nr_sequencia 	= nr_sequencia_w
	and	((ie_tipo_p = 'N' AND a.cd_material = cd_material_p) or
		(ie_tipo_p <> 'N' AND a.cd_material_estoque = cd_material_p));
end if;

return dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
