CREATE OR REPLACE FUNCTION obter_data_ult_transf (nr_atendimento_p bigint, cd_pessoa_fisica_p text default null) RETURNS timestamp AS $body$
DECLARE

dt_transfusao_w	timestamp;

BEGIN

select 	max(dt_transfusao)
into STRICT	dt_transfusao_w
from	san_transfusao
where	((nr_atendimento = nr_atendimento_p) or (cd_pessoa_fisica = cd_pessoa_fisica_p and coalesce(nr_atendimento::text, '') = ''))
and	ie_status <> 'C';

return	dt_transfusao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
