CREATE OR REPLACE FUNCTION obter_data_uso_medic ( nr_atendimento_p bigint, cd_material_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

/* 'I' - Inicial  'F' - Final */

dt_inicial_w		timestamp;
dt_final_w		timestamp;
dt_resultado_w		timestamp;



BEGIN
select 	min(b.dt_prescricao),
	min(b.dt_prescricao + coalesce(a.qt_dias_liberado,0))
into STRICT	dt_inicial_w,
	dt_final_w
from 	prescr_medica b,
	prescr_material a
where 	a.nr_prescricao 	= b.nr_prescricao
and 	b.nr_atendimento 	= nr_atendimento_p
and 	a.cd_material 		= cd_material_p;

if (ie_opcao_p 	= 'I') then
	dt_resultado_w	:= dt_inicial_w;
else
	dt_resultado_w	:= dt_final_w;
end if;


return dt_resultado_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
