CREATE OR REPLACE FUNCTION obter_data_uteis_retroativo ( cd_estabelecimento_p bigint, dt_referencia_p timestamp, qt_dias_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_referencia_w		timestamp;
qt_dias_w			smallint	:= 0;


BEGIN

dt_referencia_w	:= dt_referencia_p;

while(qt_dias_w < qt_dias_p) loop

	dt_referencia_w	:= dt_referencia_w - 1;
	qt_dias_w		:= qt_dias_w +1;

	select	obter_dia_util_anterior(cd_estabelecimento_p, dt_referencia_w)
	into STRICT	dt_referencia_w
	;

end loop;

return dt_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
