CREATE OR REPLACE FUNCTION obter_data_validade_prescr (nr_prescricao_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_validade_w		timestamp;
dt_liberacao_w	timestamp;
dt_prim_horario_w	timestamp;
nr_atendimento_w	bigint;


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	/*N達o pode colocar TO_DATE sen達o o Oracle n達o retorna o ano corretamente, estava retornando por exemplo 22/10/0010 e n達o 22/10/2010*/

	select	max(dt_validade_prescr)
	into STRICT	dt_validade_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

end if;

return dt_validade_w;

end;
$body$
