CREATE OR REPLACE FUNCTION obter_data_valida (qt_dia_p bigint, dt_mesano_p timestamp) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

if ((to_char(last_day(dt_mesano_p), 'dd'))::numeric  < qt_dia_p) then
	dt_retorno_w	:= last_day(dt_mesano_p);
elsif (qt_dia_p <= 0) then
	dt_retorno_w	:= trunc(dt_mesano_p, 'month');
else
	dt_retorno_w	:= to_date(to_char(qt_dia_p, '00') || '/' || to_char(dt_mesano_p, 'mm/yyyy'), 'dd/mm/yyyy');
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
