CREATE OR REPLACE FUNCTION obter_data_venc_reapres ( dt_parametro_p timestamp, cd_convenio_p bigint) RETURNS timestamp AS $body$
DECLARE

 
dt_retorno_w		timestamp;
qt_dias_w		smallint;
cd_estabelecimento_w	bigint;
qt_dias_reap_estab_w	bigint;


BEGIN 
 
cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
 
select	coalesce(max(a.qt_dias_reapre),0) 
into STRICT	qt_dias_reap_estab_w 
from	convenio_estabelecimento a 
where	a.cd_convenio		= cd_convenio_p 
and	a.cd_estabelecimento	= cd_estabelecimento_w;
 
select	coalesce(coalesce(qt_dias_reap_estab_w,qt_dias_reapresentacao),0) 
into STRICT	qt_dias_w 
from	convenio 
where	cd_convenio	= cd_convenio_p;
 
dt_retorno_w := dt_parametro_p;
 
if (qt_dias_w > 0) then 
	begin 
	dt_retorno_w := dt_parametro_p + qt_dias_w;
	exception 
		when others then 
			dt_retorno_w	:= null;
	end;
end if;
 
return dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
