CREATE OR REPLACE FUNCTION obter_data_venc_titulo ( nr_interno_conta_p bigint, nr_seq_protocolo_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;


BEGIN

select	max(coalesce(dt_pagamento_previsto, null))
into STRICT	dt_retorno_w
from	titulo_receber
where	nr_interno_conta	= nr_interno_conta_p;

if (coalesce(dt_retorno_w::text, '') = '') then

	begin

	select	max(coalesce(dt_pagamento_previsto, null))
	into STRICT	dt_retorno_w
	from	titulo_receber
	where	nr_seq_protocolo	= nr_seq_protocolo_p;

	exception
		when others then
			dt_retorno_w	:= null;
	end;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
