CREATE OR REPLACE FUNCTION obter_data_vigencia_dt (NR_COT_COMPRA_P bigint) RETURNS timestamp AS $body$
DECLARE

/** 	Juliane Menin - 21/01/2008
*	Obtem a data de vigência de uma cotação **/
DATA_VIGENCIA_W		timestamp;


BEGIN

IF (NR_COT_COMPRA_P IS NOT NULL AND NR_COT_COMPRA_P::text <> '') THEN
	BEGIN

	SELECT	DT_RETORNO_PREV
	INTO STRICT	DATA_VIGENCIA_W
	FROM	COT_COMPRA
	WHERE 	NR_COT_COMPRA	= NR_COT_COMPRA_P;

	END;
END IF;

RETURN	DATA_VIGENCIA_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_data_vigencia_dt (NR_COT_COMPRA_P bigint) FROM PUBLIC;

