CREATE OR REPLACE FUNCTION obter_data_vigencia (NR_COT_COMPRA_P bigint) RETURNS varchar AS $body$
DECLARE

/*	Juliane Menin - 21/01/2008 
*	Obtem a data de vigencia de uma cotacao */
DATA_VIGENCIA_W		varchar(20);


BEGIN

IF (NR_COT_COMPRA_P IS NOT NULL AND NR_COT_COMPRA_P::text <> '') THEN
	BEGIN

	SELECT	TO_CHAR(DT_RETORNO_PREV,'DD/MM/YYYY HH24:MI:SS')
	INTO STRICT	DATA_VIGENCIA_W
	FROM	COT_COMPRA
	WHERE 	NR_COT_COMPRA	= NR_COT_COMPRA_P;

	END;
END IF;

RETURN	DATA_VIGENCIA_W;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
