SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dates_isolamento (nr_atendimento_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE


nr_seq_motivo_atend_w		bigint;
nr_seq_motivo_atend_f_w		bigint;
dt_inicio_isolamento_w		timestamp;
dt_fim_isolamento_w		timestamp;


BEGIN

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_seq_motivo_atend_w
from	motivo_isolamento_atend
where	nr_atendimento = nr_atendimento_p;

select	coalesce(max(nr_Sequencia),0)
into STRICT	nr_seq_motivo_atend_f_w
from	motivo_isolamento_atend
where	nr_atendimento = nr_atendimento_p
and	coalesce(dt_fim_isolamento::text, '') = '';

if	(nr_seq_motivo_atend_w > 0 AND ie_opcao_p <> 'DSF') then
	select  dt_inicio_isolamento,
		dt_fim_isolamento
	into STRICT	dt_inicio_isolamento_w,
		dt_fim_isolamento_w
	from	motivo_isolamento_atend
	where	nr_sequencia = CASE WHEN nr_seq_motivo_atend_f_w=0 THEN nr_seq_motivo_atend_w  ELSE nr_seq_motivo_atend_f_w END;

end if;

if (upper(ie_opcao_p) = 'I') then
	return	dt_inicio_isolamento_w;
elsif (upper(ie_opcao_p) = 'F') then
	return	dt_fim_isolamento_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
