CREATE OR REPLACE FUNCTION obter_deficiencia_paciente_pc (cd_pessoa_fisica_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);


BEGIN

select	obter_select_concatenado_bv('select	substr(rp_obter_desc_deficiencia(b.nr_seq_tipo_def),1,255) ' ||
									'from	pf_tipo_deficiencia b ' ||
