SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_delta_peso ( nr_atendimento_p bigint, dt_registro_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


qt_delta_peso_w 	double precision;
qt_peso_w		double precision;
ds_retorno_w		double precision;


BEGIN

select 	max(a.qt_delta_peso),
	max(a.qt_peso)
into STRICT	qt_delta_peso_w,
	qt_peso_w
from	atendimento_sinal_vital a
where 	a.nr_atendimento = nr_atendimento_p
and		a.nr_sequencia	= (	SELECT	max(x.nr_sequencia)
							from	atendimento_sinal_vital x
							where	x.nr_atendimento	= nr_atendimento_p
							and	trunc(x.dt_sinal_vital,'dd') = trunc(dt_registro_p,'dd'));

if (ie_opcao_p = 'D') then
	ds_retorno_w := qt_delta_peso_w;
elsif (ie_opcao_p = 'P') then
	ds_retorno_w := qt_peso_w;
end if;



return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
