CREATE OR REPLACE FUNCTION obter_densidade_corporal ( cd_pessoa_fisica_p text, qt_soma_pregas_cutaneas_p bigint, qt_peso_atual_p bigint, qt_altura_cm_p bigint) RETURNS bigint AS $body$
DECLARE


ie_sexo_pf_w			varchar(1);
qt_idade_pf_w			smallint;
qt_densidade_corporal_w		aval_nutricao.qt_densidade_corporal%type;

BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (qt_soma_pregas_cutaneas_p IS NOT NULL AND qt_soma_pregas_cutaneas_p::text <> '') then

	ie_sexo_pf_w := substr(Obter_Sexo_PF(cd_pessoa_fisica_p, 'C'), 1, 1);
	qt_idade_pf_w := Campo_numerico(Obter_Idade_PF(cd_pessoa_fisica_p, clock_timestamp(), 'A'));

	if (ie_sexo_pf_w IS NOT NULL AND ie_sexo_pf_w::text <> '') and (qt_idade_pf_w IS NOT NULL AND qt_idade_pf_w::text <> '') and (qt_idade_pf_w > 17) and (qt_idade_pf_w <  67) then

		if (ie_sexo_pf_w = 'M') then
			qt_densidade_corporal_w := 1.10726863 - 0.00081201 * qt_soma_pregas_cutaneas_p + 0.00000212 * power(qt_soma_pregas_cutaneas_p, 2) - 0.00041761 * qt_idade_pf_w;

		elsif (ie_sexo_pf_w = 'F') and (qt_peso_atual_p IS NOT NULL AND qt_peso_atual_p::text <> '') and (qt_altura_cm_p IS NOT NULL AND qt_altura_cm_p::text <> '') then
			qt_densidade_corporal_w := 1.02902361 - 0.00067159 * qt_soma_pregas_cutaneas_p + 0.00000242 * power(qt_soma_pregas_cutaneas_p, 2) - 0.00026073 * qt_idade_pf_w - 0.00056009 * qt_peso_atual_p + 0.00054649 * qt_altura_cm_p;

		end if;

	end if;

end if;

return	qt_densidade_corporal_w;

end;
