CREATE OR REPLACE FUNCTION obter_departamento_atual_atend (nr_atendimento_p atendimento_paciente.nr_atendimento%type) RETURNS bigint AS $body$
DECLARE


cd_departamento_w departamento_medico.cd_departamento%type;
			

BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(apu.cd_departamento)
	into STRICT 	cd_departamento_w
	from	atend_paciente_unidade apu
	where	apu.nr_seq_interno = obter_atepacu_paciente(nr_atendimento_p, 'A');
end if;

return	cd_departamento_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
