SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_departamento_medico ( cd_pessoa_fisica_p text, ie_short_name_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(200);
ds_departamento_w varchar(100);

c01 CURSOR FOR
	SELECT	substr(coalesce(CASE WHEN ie_short_name_p='S' THEN  coalesce(c.ds_short_name, c.ds_departamento)  ELSE c.ds_departamento END , d.ds_setor_atendimento),1,100)
  from    medico a,
          departamento_setor b,
          departamento_medico c,
          setor_atendimento d
  where a.cd_setor_atendimento = b.cd_setor_atendimento
  and   b.cd_departamento = c.cd_departamento
  and   a.cd_setor_atendimento = d.cd_setor_atendimento
  and   a.cd_pessoa_fisica = cd_pessoa_fisica_p
  and   a.ie_situacao = 'A'
  and   c.ie_situacao = 'A'
  and   d.ie_situacao = 'A';


BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin
        ds_retorno_w := null;
				
        open c01;
				loop
				fetch c01 into
					ds_departamento_w;
				EXIT WHEN NOT FOUND; /* apply on c01 */
          ds_retorno_w := substr(ds_retorno_w || ' - ' || ds_departamento_w, 1, 200);
				end loop;
				close c01;

	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
