CREATE OR REPLACE FUNCTION obter_dependencia_etapa (NR_SEQ_PENDENCIA_P bigint ,NR_SEQUENCIA_P bigint ,IE_OPCAO_P text DEFAULT 'N',NR_SEQ_ETAPA_DUP_P bigint DEFAULT NULL) RETURNS varchar AS $body$
DECLARE


  nivel_w   bigint;
  ds_path_w varchar(4000);
  return_w  varchar(4000);WITH RECURSIVE cte AS (


  c_nivel CURSOR FOR
    SELECT max(1)
      FROM gqa_pendencia_regra t
     WHERE t.nr_seq_pendencia = NR_SEQ_PENDENCIA_P
       AND t.nr_sequencia = NR_SEQUENCIA_P  UNION ALL

  
  c_nivel CURSOR FOR
    SELECT max(c.level+1)
      FROM gqa_pendencia_regra t
     WHERE t.nr_seq_pendencia = NR_SEQ_PENDENCIA_P
       AND t.nr_sequencia = NR_SEQUENCIA_P JOIN cte c ON (c.nr_sequencia = t.nr_seq_regra_sup)

) SELECT * FROM cte;
;WITH RECURSIVE cte AS (


  c_path CURSOR(nivel_p bigint) FOR
    SELECT substr(t.ds_regra||NR_SEQ_ETAPA_DUP_P,4,4000)
      FROM gqa_pendencia_regra t
     WHERE t.nr_seq_pendencia = NR_SEQ_PENDENCIA_P
       AND t.nr_sequencia = NR_SEQUENCIA_P
       AND level = nivel_p  UNION ALL


  c_path CURSOR(nivel_p bigint) FOR
    SELECT c. || '<..>' || substr(t.ds_regra||NR_SEQ_ETAPA_DUP_P,4,4000)
      FROM gqa_pendencia_regra t
     WHERE t.nr_seq_pendencia = NR_SEQ_PENDENCIA_P
       AND t.nr_sequencia = NR_SEQUENCIA_P
       AND level = nivel_p JOIN cte c ON (c.nr_sequencia = t.nr_seq_regra_sup)

) SELECT * FROM cte;
;


BEGIN
  OPEN c_nivel;
 FETCH c_nivel
  INTO nivel_w;
 CLOSE c_nivel;

  IF (IE_OPCAO_P = 'N') THEN
    return_w := nivel_w;
  ELSE
    OPEN c_path(nivel_w);
   FETCH c_path
    INTO ds_path_w;
   CLOSE c_path;

    return_w := ds_path_w;
  END IF;

  RETURN return_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
