CREATE OR REPLACE FUNCTION obter_deptos_medico (cd_medico_p bigint) RETURNS varchar AS $body$
DECLARE

nm_depto_w		varchar(255);
ds_retorno_w	varchar(255);

c01 CURSOR FOR 
SELECT	d.ds_depto 
from	depto_medico d, 
		medico_especialidade c, 
		medico a, 
		depto_medico_espec b 
where	c.cd_pessoa_fisica = a.cd_pessoa_fisica 
and		c.cd_especialidade	= b.cd_especialidade 
and		b.nr_seq_depto		= d.nr_sequencia 
and		a.cd_pessoa_fisica	= cd_medico_p 
order by 1;


BEGIN 
	open c01;
	loop 
	fetch c01 into 
		nm_depto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		ds_retorno_w:=ds_retorno_w||' '||nm_depto_w||',';
	end loop;
 
return	substr(ds_retorno_w,2,length(ds_retorno_w)-2);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
