CREATE OR REPLACE FUNCTION obter_depto_medico_agenda ( nr_seq_lista_p bigint, ie_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


cd_depto_medico_w	agenda_lista_espera.cd_departamento_medico%type;
ds_return_w		varchar(255) := '';


BEGIN

if (coalesce(nr_seq_lista_p, 0) > 0)then
	
	select	max(al.cd_departamento_medico)
	into STRICT	cd_depto_medico_w
	from	agenda_lista_espera al
	where 	nr_sequencia = nr_seq_lista_p;
	
	if (coalesce(cd_depto_medico_w, 0) > 0)then
		if (ie_tipo_p = 1)then
			ds_return_w := obter_nome_departamento_medico(cd_depto_medico_w);
		elsif (ie_tipo_p = 2)then
			ds_return_w := obter_nome_curto_depto_medico(cd_depto_medico_w);	
		end if;
	end if;
end if;

return	ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
