CREATE OR REPLACE FUNCTION obter_depto_medico_nome_curto ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


cd_depto_medico_w	agenda_paciente_auxiliar.cd_depto_medico%type;
ds_return_w		varchar(255) := '';


BEGIN

if (coalesce(nr_sequencia_p, 0) > 0)then

	select  max(cd_depto_medico)
	into STRICT	cd_depto_medico_w
	from	agenda_paciente_auxiliar a
	where   nr_seq_agenda = nr_sequencia_p;
	
	if (coalesce(cd_depto_medico_w, 0) > 0)then
		ds_return_w := obter_nome_curto_depto_medico(cd_depto_medico_w);	
	end if;
end if;

return	ds_return_w;

end;
$body$
