CREATE OR REPLACE FUNCTION obter_dept_lib_usuario ( cd_departamento_p bigint, nm_usuario_p text, cd_pessoa_fisica_p text default null) RETURNS varchar AS $body$
DECLARE


ie_dept_liberado_usuario	varchar(1) := 'N';


BEGIN

if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	select	coalesce(max('S'), 'N')
	into STRICT	ie_dept_liberado_usuario
	from 	usuario_setor a, 
			departamento_setor b 
	where 	a.nm_usuario = nm_usuario_p 
	and 	a.cd_setor_atendimento = b.cd_setor_atendimento 
	and 	cd_departamento in (cd_departamento_p);
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	coalesce(max('S'), 'N')
	into STRICT	ie_dept_liberado_usuario
	from 	usuario_setor a, 
			departamento_setor b 
	where 	a.nm_usuario = obter_usuario_pessoa(cd_pessoa_fisica_p) 
	and 	a.cd_setor_atendimento = b.cd_setor_atendimento 
	and 	cd_departamento in (cd_departamento_p);	
end if;
	
return	ie_dept_liberado_usuario;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
