SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_derivados_ficha_medica (nr_seq_ficha_p bigint) RETURNS varchar AS $body$
DECLARE

c01 CURSOR FOR
	SELECT 	a.nr_seq_dcb
	from 	medic_ficha_tecnica a
	where 	a.nr_seq_superior = nr_seq_ficha_p
	and 	not exists (SELECT x.nr_sequencia
						from 	material_interacao_medic x
						where 	x.nr_seq_ficha = a.nr_sequencia);

ie_composto_w	varchar(1);
ds_retorno_w	varchar(4000);
nr_seq_ficha_w	bigint;

BEGIN
	ds_retorno_w := null;

	select 	coalesce(max('S'), 'N')
	into STRICT 	ie_composto_w
	from 	medic_ficha_tecnica
	where 	nr_sequencia = nr_seq_ficha_p
	and 	coalesce(nr_seq_superior::text, '') = '';

	if (ie_composto_w = 'S') then
		for c01_w in C01
		loop
			select 	max(nr_sequencia)
			into STRICT 	nr_seq_ficha_w
			from 	medic_ficha_tecnica
			where 	nr_seq_dcb = c01_w.nr_seq_dcb
			and 	coalesce(nr_seq_superior::text, '') = '';
			
			if (nr_seq_ficha_w IS NOT NULL AND nr_seq_ficha_w::text <> '') then
				if (coalesce(ds_retorno_w::text, '') = '') then
					ds_retorno_w := nr_seq_ficha_w;
				else
					ds_retorno_w := ds_retorno_w || ',' || nr_seq_ficha_w;
				end if;
			end if;
		end loop;
	end if;
	
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w := nr_seq_ficha_p;
	end if;

	return ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
