CREATE OR REPLACE FUNCTION obter_derivados_lactario (nr_seq_item_Lac_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
ds_material_w		varchar(80);
cd_unidade_medida_w	varchar(20);
pr_dose_w		double precision;
qt_dose_w		double precision;

C01 CURSOR FOR
	SELECT	b.ds_material,
		a.pr_dose,
		a.qt_dose,
		c.cd_unidade_medida
	from	nut_producao_lac_item_adic a,
		prescr_material c,
		material b
	where	a.cd_material 		= b.cd_material
	and	a.nr_prescricao		= c.nr_prescricao
	and	a.nr_seq_material	= c.nr_Sequencia
	and	a.nr_Seq_prod_item	= nr_seq_item_Lac_p;


BEGIN

open C01;
loop
fetch C01 into
	ds_material_w,
	pr_dose_w,
	qt_dose_w,
	cd_unidade_medida_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (ie_opcao_p = 0) then
		ds_retorno_w	:= substr(ds_retorno_w || ',' || ds_material_w || '(' || pr_dose_w ||'% '|| qt_dose_w || cd_unidade_medida_w || ')' ,1,255);
	elsif (ie_opcao_p = 1)then
		ds_retorno_w	:= substr(ds_retorno_w || ',' || ds_material_w,1,255);
	elsif (ie_opcao_p = 2) and (pr_dose_W IS NOT NULL AND pr_dose_W::text <> '') then
		ds_retorno_w	:= substr(ds_retorno_w || ',' || pr_dose_W,1,255);
	elsif (ie_opcao_p = 3) and (qt_dose_w IS NOT NULL AND qt_dose_w::text <> '') then
		ds_retorno_w	:= substr(ds_retorno_w || ',' || qt_dose_w,1,255);
	elsif (ie_opcao_p = 4) and (cd_unidade_medida_w IS NOT NULL AND cd_unidade_medida_w::text <> '') then
		ds_retorno_w	:= substr(ds_retorno_w || ',' || cd_unidade_medida_w,1,255);
	end if;

	end;
end loop;
close C01;

ds_retorno_w	:= substr(ds_retorno_w,2,length(ds_retorno_w));

if (ie_opcao_p = 3) then
	begin
	ds_retorno_w	:= (ds_retorno_w)::numeric;
	exception
	when others then
		ds_retorno_w	:= null;
	end;
end if;

return	ds_retorno_w;

end;
