CREATE OR REPLACE FUNCTION obter_desconto_financ_contr ( nr_seq_contrato_p bigint) RETURNS bigint AS $body$
DECLARE


pr_desc_financ_w	contrato_regra_pagto.pr_desc_financ%type;


BEGIN

if (nr_seq_contrato_p IS NOT NULL AND nr_seq_contrato_p::text <> '') then

    begin
        select	coalesce(pr_desc_financ,0)
        into STRICT	pr_desc_financ_w
        from	contrato_regra_pagto where nr_seq_contrato = nr_seq_contrato_p
        and	((coalesce(dt_inicio_vigencia::text, '') = '') or (trunc(dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd')))
        and	((coalesce(dt_final_vigencia::text, '') = '') or (trunc(dt_final_vigencia,'dd') >= trunc(clock_timestamp(),'dd')))
        order by nr_sequencia desc LIMIT 1;
    exception when no_data_found or too_many_rows then
          pr_desc_financ_w := 0;
    end;

end if;

return	pr_desc_financ_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
