SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desconto_item_conta (nr_seq_matproc_p bigint, ie_matproc_p bigint) RETURNS bigint AS $body$
DECLARE


vl_desconto_w		double precision	:= 0;
nr_interno_conta_w	bigint;
ie_tipo_desconto_w	bigint;


BEGIN

if (ie_matproc_p	= 1) then
	begin

	select	max(nr_interno_conta)
	into STRICT	nr_interno_conta_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_matproc_p;

	select	coalesce(max(ie_tipo_desconto),0)
	into STRICT	ie_tipo_desconto_w
	from	conta_paciente_desconto
	where	nr_interno_conta	= nr_interno_conta_w;

	if (ie_tipo_desconto_w = 7) then
		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_w
		from	conta_paciente_desc_item
		where	nr_seq_proc_pacote	= nr_seq_matproc_p;
	else
		select	coalesce(sum(vl_procedimento),0)
		into STRICT	vl_desconto_w
		from	proc_paciente_valor
		where	ie_tipo_valor		= 3
		and	nr_seq_procedimento	= nr_seq_matproc_p;
	end if;

	end;
elsif (ie_matproc_p	= 2) then
	begin

	select	coalesce(sum(vl_material),0)
	into STRICT	vl_desconto_w
	from	mat_atend_paciente_valor
	where	ie_tipo_valor		= 3
	and	nr_seq_material		= nr_seq_matproc_p;

	end;
end if;

return	vl_desconto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
