SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desconto_matproc (nr_seq_matproc_p bigint, ie_matproc_p text) RETURNS bigint AS $body$
DECLARE


vl_desconto_w		double precision	:= 0;
nr_interno_conta_w	bigint;
ie_tipo_desconto_w	bigint;

nr_seq_proc_pacote_w	bigint;
nr_seq_item_w		bigint;
ie_proc_mat_w		smallint;
vl_desc_proc_w		double precision;
vl_desc_mat_w		double precision;


C01 CURSOR FOR
	SELECT	nr_sequencia,
		1 ie_proc_mat
	from	procedimento_paciente
	where	nr_seq_proc_pacote = coalesce(nr_seq_proc_pacote_w,0)
	and 	nr_interno_conta = nr_interno_conta_w
	
union all

	SELECT	nr_sequencia,
		2 ie_proc_mat
	from	material_atend_paciente
	where	nr_seq_proc_pacote = coalesce(nr_seq_proc_pacote_w,0)
	and 	nr_interno_conta = nr_interno_conta_w
	order by ie_proc_mat;


BEGIN

if (ie_matproc_p	= 'P') then
	begin

	select	max(nr_interno_conta),
		max(nr_seq_proc_pacote)
	into STRICT	nr_interno_conta_w,
		nr_seq_proc_pacote_w
	from	procedimento_paciente
	where	nr_sequencia	= nr_seq_matproc_p;

	select	coalesce(max(ie_tipo_desconto),0)
	into STRICT	ie_tipo_desconto_w
	from	conta_paciente_desconto
	where	nr_interno_conta	= nr_interno_conta_w;

	if (ie_tipo_desconto_w = 7) then
		select	coalesce(sum(vl_desconto),0)
		into STRICT	vl_desconto_w
		from	conta_paciente_desc_item
		where	nr_seq_proc_pacote	= nr_seq_matproc_p;
	else

		if (coalesce(nr_seq_proc_pacote_w,0) = nr_seq_matproc_p) then

			vl_desc_mat_w	:= 0;
			vl_desc_proc_w	:= 0;
			vl_desconto_w	:= 0;

			open C01;
			loop
			fetch C01 into
				nr_seq_item_w,
				ie_proc_mat_w;
			EXIT WHEN NOT FOUND; /* apply on C01 */
				begin

				if (ie_proc_mat_w = 1) then

					select	coalesce(sum(vl_procedimento),0)
					into STRICT	vl_desc_proc_w
					from	proc_paciente_valor
					where	ie_tipo_valor		= 3
					and	nr_seq_procedimento	= nr_seq_item_w;

					vl_desconto_w:= vl_desconto_w + vl_desc_proc_w;

				else
					select	coalesce(sum(vl_material),0)
					into STRICT	vl_desc_mat_w
					from	mat_atend_paciente_valor
					where	ie_tipo_valor		= 3
					and	nr_seq_material		= nr_seq_item_w;

					vl_desconto_w:= vl_desconto_w + vl_desc_mat_w;

				end if;

				end;
			end loop;
			close C01;

		else

			select	coalesce(sum(vl_procedimento),0)
			into STRICT	vl_desconto_w
			from	proc_paciente_valor
			where	ie_tipo_valor		= 3
			and	nr_seq_procedimento	= nr_seq_matproc_p;

		end if;
	end if;

	end;
elsif (ie_matproc_p	= 'M') then
	begin

	select	coalesce(sum(vl_material),0)
	into STRICT	vl_desconto_w
	from	mat_atend_paciente_valor
	where	ie_tipo_valor		= 3
	and	nr_seq_material		= nr_seq_matproc_p;

	end;
end if;

return	vl_desconto_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
