CREATE OR REPLACE FUNCTION obter_desconto_tit_rec ( nr_titulo_p bigint) RETURNS bigint AS $body$
DECLARE


tx_desc_antecipacao_w		double precision;
vl_titulo_w			double precision;
vl_desconto_w			double precision;
vl_desc_previsto_w		double precision;


BEGIN

select	coalesce(tx_desc_antecipacao,0),
	vl_titulo,
	coalesce(vl_desc_previsto,0)
into STRICT	tx_desc_antecipacao_w,
	vl_titulo_w,
	vl_desc_previsto_w
from	titulo_receber
where	nr_titulo = nr_titulo_p;

if (vl_desc_previsto_w = 0) then
	vl_desconto_w := ((vl_titulo_w / 100) * tx_desc_antecipacao_w);

else	vl_desconto_w := vl_desc_previsto_w;

end if;

RETURN 	coalesce(vl_desconto_w, 0);

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
