CREATE OR REPLACE FUNCTION obter_descricao_ativ_desenv ( nr_seq_atividade_p bigint) RETURNS varchar AS $body$
DECLARE



ds_retorno_w			varchar(255);
cd_exp_atividade_w		des_ativ_exec.cd_exp_atividade%type;


BEGIN

select 	substr(obter_desc_expressao(max(cd_exp_atividade),max(ds_atividade)),1,255)
into STRICT	ds_retorno_w
from 	des_ativ_exec
where 	nr_sequencia	= nr_seq_atividade_p;

return ds_retorno_w;

end;
