CREATE OR REPLACE FUNCTION obter_descricao_caixa_opme ( nr_seq_caixa_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE

nr_seq_tipo_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

select	nr_seq_tipo
into STRICT	nr_seq_tipo_w
from	opme_caixa
where	nr_sequencia	=	nr_seq_caixa_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_seq_tipo_w;
elsif (ie_opcao_p = 'D') then
	select	substr(obter_descricao_padrao('OPME_TIPO_CAIXA','DS_TIPO_CAIXA',nr_seq_tipo),1,100)
	into STRICT	ds_retorno_w
	from	opme_caixa
	where	nr_sequencia	=	nr_seq_caixa_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
