CREATE OR REPLACE FUNCTION obter_descricao_cid (cd_doenca_cid_p text) RETURNS varchar AS $body$
DECLARE


ds_informacao_adic_w  varchar(2000);


BEGIN

if (cd_doenca_cid_p IS NOT NULL AND cd_doenca_cid_p::text <> '') then
	select 	max(ds_informacao_adic)
	 into STRICT	ds_informacao_adic_w
	 from	cid_doenca
	 where	cd_doenca_cid = cd_doenca_cid_p;
end if;

return ds_informacao_adic_w;

end;
