CREATE OR REPLACE FUNCTION obter_descricao_contrato ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_contrato_w		varchar(255);
ie_classificacao_w	varchar(2);

 

BEGIN 
 
select	substr(obter_nome_pf_pj(cd_pessoa_contratada,cd_cgc_contratado),1,60), 
	ie_classificacao 
into STRICT	ds_contrato_w, 
	ie_classificacao_w 
from	contrato 
where	nr_sequencia = nr_sequencia_p;
 
if (ie_classificacao_w in ('ER','AD')) then 
	ds_contrato_w	:= '     ' || ds_contrato_w;
end if;
 
return ds_contrato_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
