CREATE OR REPLACE FUNCTION obter_descricao_dominio (cd_dominio_p bigint, vl_dominio_p text) RETURNS varchar AS $body$
DECLARE


ds_valor_dominio_w		varchar(254);

BEGIN
begin
select	substr(ds_expressao,1,254)
into STRICT		ds_valor_dominio_w
from 		valor_dominio_v
where 	cd_dominio	= cd_dominio_p
  and		vl_dominio	= vl_dominio_p;
exception
	when others then
		ds_valor_dominio_w	:= null;
end;
return ds_valor_dominio_w;
END;
$body$
LANGUAGE PLPGSQL
