SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_descricao_elemento ( nr_seq_header_p bigint, ds_elemento_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_text_w           varchar(255);
ds_description_w    varchar(255);
ds_retorno_w        varchar(255);

ie_opcao_text_w     varchar(2) := 'T';
ie_description_w    varchar(2) := 'D';


BEGIN

SELECT substr(a.DS_TEXT,1, 255),
       substr(a.DS_DESCRIPTION ,1,255)
into STRICT   ds_text_w,
       ds_description_w
FROM   EHR_ARQUETIPO_TERMS  a,
       EHR_ARQUETIPO_HEADER b
WHERE  b.nr_sequencia = nr_seq_header_p
AND    b.nr_sequencia = a.NR_SEQ_HEADER
AND    a.ds_code = ds_elemento_p;

case ie_opcao_p

    when ie_opcao_text_w then
        ds_retorno_w := ds_text_w;

    when ie_description_w then
        ds_retorno_w := ds_description_w;

end case;


RETURN ds_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
