CREATE OR REPLACE FUNCTION obter_descricao_intervalo (cd_intervalo_p text) RETURNS varchar AS $body$
DECLARE


ds_intervalo_p		varchar(100);

BEGIN

select 	ds_intervalo
into STRICT	ds_intervalo_p
from 	intervalo_prescricao
where 	ie_situacao = 'A'
and 	ie_prescricao_dieta in ('N','H','E')
and	cd_intervalo = cd_intervalo_p;

return	ds_intervalo_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
