SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_descricao_item_eif ( nr_seq_item_p bigint, ie_cons_titulo_p text default 'N') RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ie_titulo_w		varchar(1);
qt_reg_w		bigint;
nr_seq_escala_w bigint;


BEGIN

select	ds_item, coalesce(ie_titulo,'N'), nr_seq_escala
into STRICT	ds_retorno_w, ie_titulo_w, nr_seq_escala_w
from	eif_escala_item
where	nr_sequencia = nr_seq_item_p;

select	count(*)
into STRICT	qt_reg_w
from	eif_escala_item
where	nr_seq_escala = nr_seq_escala_w
and		coalesce(ie_titulo,'N') = 'S';

if (ie_cons_titulo_p = 'S') and (qt_reg_w > 0) and (ie_titulo_w = 'N') then
	ds_retorno_w := substr('       ' || ds_retorno_w,1,255);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
