CREATE OR REPLACE FUNCTION obter_descricao_justificativa ( nr_cirurgia_p bigint, cd_material_p bigint ) RETURNS varchar AS $body$
DECLARE


ds_justificativa_w	varchar(2000);


BEGIN

select	max(ds_justificativa)
into STRICT	ds_justificativa_w
from	cirurgia_agente_disp
where	nr_cirurgia	=	nr_cirurgia_p
and	cd_material	=	cd_material_p;

if (coalesce(ds_justificativa_w::text, '') = '') then
	select	max(ds_justificativa)
	into STRICT	ds_justificativa_w
	from	cirurgia_agente_disp
	where	nr_cirurgia	=	nr_cirurgia_p
	and	cd_material in (	SELECT	cd_material
					from	material
					where	cd_material_generico = cd_material_p);
end if;

return	ds_justificativa_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
