CREATE OR REPLACE FUNCTION obter_descricao_kit_montagem (cd_tipo_kit_p text, cd_especialidade_p bigint, cd_estabelecimento_p bigint, cd_local_estoque_p bigint, cd_kit_material_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w  kit_material.ds_kit_material%type;


BEGIN


select  coalesce(ds_kit_material,' ')
into STRICT	retorno_w
from	 kit_material
where ((cd_tipo_kit_p = '0')
or    (cd_tipo_kit_p > '0' AND ie_tipo = cd_tipo_kit_p)
or (coalesce(cd_tipo_kit_p,'0') = '0'))
and   ((cd_especialidade_p = 0) 
or    (cd_especialidade_p > 0 AND cd_especialidade_medica = cd_especialidade_p)                                      
or (coalesce(cd_especialidade_p,0) = 0))
and   ie_situacao = 'A'
and   ((coalesce(cd_estab_exclusivo,cd_estabelecimento_p) = cd_estabelecimento_p) 
or (cd_estabelecimento_p = 0))
and   ((coalesce(cd_local_exclusivo, cd_local_estoque_p) = cd_local_estoque_p)                  
or (coalesce(cd_local_estoque_p,0) = 0))
and   cd_kit_material = coalesce(cd_kit_material_p,cd_kit_material)
order by ds_kit_material;


return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
